import './App.css';
import Tabs from './shared/components/Tab/Tab';
import Test from './dishes/pages/Test';
import MainNavigation from './shared/components/Navigation/MainNavigation';
import { useDish } from './shared/hooks/dish-hook';
import { DishContext } from './shared/context/dish-context';
import MainHead from './MainHead';
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from 'react-router-dom';

function App() {
  const {restaurantName,category,dishes,setRestname,setCategory,setDishes} = useDish()
  console.log(dish.category)
  let routes = (
    <Switch>
      <Route path="/" exact>
        <Test />
      </Route>
      <Route path="/dishes/:categoryId" exact>
        <Test />
      </Route>   
      <Redirect to="/" />
    </Switch>
  );

  return (
    <DishContext.Provider
    value={{
     restaurantName:restaurantName,
     category:category,
     dishes:dishes,
     setRestname:setRestname,
     setCategory:setCategory,
     setDishes:setDishes
    }}
  >
    <Router>     
    <MainHead></MainHead>
    <MainNavigation></MainNavigation>
     <main>{routes}</main>
     </Router>
     </DishContext.Provider>
  );
}
export default App;
