import React, { useState,useContext,useEffect } from 'react';
import { Link } from 'react-router-dom';
import { DishContext } from '../../context/dish-context';
import { useHttpClient } from '../../hooks/http-hook';
import MainHeader from './MainHeader';
import NavLinks from './NavLinks';
import SideDrawer from './SideDrawer';
import Backdrop from '../UIElements/Backdrop';
import LoadingSpinner from '../UIElements/LoadingSpinner';
import ErrorModal from '../UIElements/ErrorModal';
import './MainNavigation.css';

const MainNavigation = props => {
     const dish = useContext(DishContext);
     const { isLoading, error, sendRequest, clearError } = useHttpClient();
     useEffect(() => {
      const fetchDishes = async () => {
        try {
          const responseData = await sendRequest(
            `https://run.mocky.io/v3/a67edc87-49c7-4822-9cb4-e2ef94cb3099`
          );
          dish.setRestname(responseData[0])
          dish.setCategory(responseData[0]);
          dish.setDishes(responseData[0]);
        } catch (err) {}
      };
      fetchDishes();
    }, [sendRequest]);


  return (

      <React.Fragment>
      <ErrorModal error={error} onClear={clearError} />
      {isLoading && (
        <div className="center">
          <LoadingSpinner />
        </div>
      )}
      <nav className="main-navigation__header-nav">
      <ul className="nav-links">
        {!isLoading && dish.category.length  &&dish.category.map(cat=>{
          <NavLinks title={cat.menu_category}
           categoryId={cat.menu_category_id} />

        })}
      {/* <NavLinks title="Tab 1" id="tab1" />
      <NavLinks title="Tab 2" id="tab2" />
      <NavLinks title="Tab 3" id="tab3"/> */}
      </ul>
      </nav>
      </React.Fragment>

  );
};

export default MainNavigation;
