import React, { useState, useContext } from 'react';

import Card from '../../shared/components/UIElements/Card';
import Button from '../../shared/components/UIElements/Button';
import Modal from '../../shared/components/UIElements/Modal';
import ErrorModal from '../../shared/components/UIElements/ErrorModal';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import { DishContext } from '../../shared/context/dish-context';
import { useHttpClient } from '../../shared/hooks/http-hook';
import CartButton from './CartButton';
import {BsRecordBtn} from "react-icons/bs"
import './Dishitem.css';

const DishItem = props => {
  const { isLoading, error, sendRequest, clearError } = useHttpClient();
  const dish = useContext(DishContext);
  const [count,setCount] = useState(0)
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const countChangeHandler = (c) => {
    setCount(c)
  }


  return (
    <React.Fragment>
      <ErrorModal error={error} onClear={clearError} />

      <li className="dish-item">
        <Card className="dish-item__content">
          {isLoading && <LoadingSpinner asOverlay />}
          <div className="dish-item__info">
              <h2><BsRecordBtn colo></BsRecordBtn>{ props.title}</h2>

            <h3>{`${props.currency} ${props.price}`}</h3>
            <p>{props.description}</p>
            {/* <Button to={`/places/${props.id}`}>- 0 +</Button> */}
            {props.availability && <CartButton count={count} onChange={c=>countChangeHandler(c)}></CartButton>}
            {props.addonCat && (<p className="red">customirization available</p>)}
            {!props.availability && (<p className="red">Not available</p>)}
          </div>
          <div className="dish-cal">
            <h2>{`${props.calorie} calories`}</h2>
          </div>
          <div className="dish-item__image">
            <img
              src={props.image}
              alt={props.title}
            />
          </div>

           {/* <div className="dish-item__actions"> */}
            {/* <Button inverse onClick={openMapHandler}>
              VIEW ON MAP
            </Button> */}
              {/* {auth.userId === props.creatorId && (
            //   <Button to={`/places/${props.id}`}>EDIT</Button>
            // )}

            // {auth.userId === props.creatorId && (
            //   <Button danger onClick={showDeleteWarningHandler}>
            //     DELETE
            //   </Button> 
            // )} */}
          {/* </div>   */}
        </Card>
      </li>
    </React.Fragment>
  );
};

export default DishItem;
